css_styling.code_example:
/* Selettore elemento: applica a tutti i <p> */
p {
  color: blue;
  font-size: 16px;
}

/* Selettore classe: applica a class='highlight' */
.highlight {
  background-color: yellow;
  padding: 5px;
}

/* Selettore ID: applica a id='header' (univoco) */
#header {
  margin: 0;
  background-color: navy;
  color: white;
}

/* Selettore attributo: input con type='email' */
input[type="email"] {
  border: 2px solid blue;
  width: 100%;
}

/* Combinatori */
div p {  /* Discendente: <p> dentro <div> a qualsiasi livello */
  color: red;
}

div > p {  /* Figlio diretto: <p> figlio diretto di <div> */
  color: green;
}

h1 + p {  /* Elemento adiacente: <p> immediatamente dopo <h1> */
  color: gray;
  font-weight: bold;
}

h2 ~ p {  /* Fratelli generali: tutti i <p> dopo <h2> */
  text-indent: 20px;
}

/* Specificity: 0,0,1 (1 elemento) */
p {
  color: blue;
}

/* Specificity: 0,1,0 (1 classe) - vince su elemento */
.text {
  color: green;
}

/* Specificity: 0,1,1 (1 classe + 1 elemento) - vince su .text */
p.text {
  color: red;
}

/* Specificity: 1,0,0 (1 ID) - vince su tutto tranne inline */
#special {
  color: yellow;
}

/* Specificity: 1,1,1 (1 ID + 1 classe + 1 elemento) */
div#container .text {
  color: purple;
}

/* Inline style: specificity 1,0,0,0 (massima) */
/* <p style='color: orange;'>Testo</p> */

/* !important: bypassa specificity (evitare) */
.override {
  color: pink !important;  /* Vince su tutto */
}

/* Esempio pratico */
<style>
  p { color: blue; }           /* 0,0,1 */
  .highlight { color: green; } /* 0,1,0 - vince */
</style>

<p class="highlight">Questo testo sarà verde</p>

/* Box model standard (content-box) */
.box-standard {
  width: 300px;             /* Larghezza del CONTENT */
  height: 200px;            /* Altezza del CONTENT */
  padding: 20px;            /* Spazio interno */
  border: 5px solid black;  /* Bordo */
  margin: 10px;             /* Spazio esterno */

  /* Larghezza totale = 300 + (20*2) + (5*2) = 350px */
  /* Altezza totale = 200 + (20*2) + (5*2) = 250px */
}

/* Box model moderno (border-box) - CONSIGLIATO */
.box-modern {
  box-sizing: border-box;  /* Padding e border inclusi nella width */
  width: 300px;            /* Larghezza TOTALE (incluso padding e border) */
  height: 200px;
  padding: 20px;
  border: 5px solid black;
  margin: 10px;

  /* Larghezza totale = 300px (padding e border inclusi) */
  /* Content width = 300 - (20*2) - (5*2) = 250px */
}

/* Reset globale consigliato */
* {
  box-sizing: border-box;  /* Applica a tutti gli elementi */
  margin: 0;
  padding: 0;
}

/* Padding e margin differenziati per lato */
.box-custom {
  padding-top: 10px;
  padding-right: 20px;
  padding-bottom: 10px;
  padding-left: 20px;
  /* Shorthand: padding: 10px 20px; (top/bottom right/left) */
  /* Shorthand: padding: 10px 20px 15px 25px; (top right bottom left - senso orario) */

  margin: 20px auto;  /* margin-top/bottom: 20px, margin-left/right: auto (centra) */
}

/* Border properties */
.box-border {
  border-width: 2px;
  border-style: solid;  /* solid, dashed, dotted, double, none */
  border-color: #333;
  /* Shorthand: border: 2px solid #333; */

  border-radius: 10px;  /* Angoli arrotondati */
  border-radius: 50%;   /* Cerchio (se width = height) */
}

/* Unità assolute */
.fixed {
  width: 300px;      /* 300 pixel fissi */
  font-size: 16px;   /* 16 pixel fissi */
}

/* Unità relative al genitore (em) */
.parent {
  font-size: 20px;   /* Dimensione base del genitore */
}

.child-em {
  font-size: 1.5em;  /* 1.5 * 20px = 30px */
  padding: 0.5em;    /* 0.5 * 30px (font-size di .child-em) = 15px */
  margin: 2em;       /* 2 * 30px = 60px */
}

/* Unità relative alla root (rem) - CONSIGLIATO */
html {
  font-size: 16px;   /* Dimensione root (default browser) */
}

.child-rem {
  font-size: 1.5rem; /* 1.5 * 16px = 24px */
  padding: 1rem;     /* 1 * 16px = 16px */
  margin: 2rem;      /* 2 * 16px = 32px */
}

/* Percentuale relativa al genitore */
.container {
  width: 1000px;
}

.box-percent {
  width: 50%;        /* 50% di 1000px = 500px */
  padding: 5%;       /* 5% di 1000px = 50px */
}

/* Viewport units (relative allo schermo) */
.hero {
  width: 100vw;      /* 100% larghezza viewport */
  height: 100vh;     /* 100% altezza viewport (schermo intero) */
}

.responsive-text {
  font-size: 5vw;    /* 5% della larghezza viewport */
  /* Su schermo 1000px: 5vw = 50px */
  /* Su schermo 500px: 5vw = 25px (scala automaticamente) */
}

/* Confronto pratico */
.comparison {
  font-size: 16px;   /* Fisso: sempre 16px */
  font-size: 1em;    /* Relativo al genitore: varia per annidamento */
  font-size: 1rem;   /* Relativo a <html>: prevedibile e scalabile */
  font-size: 2vw;    /* Relativo allo schermo: responsive automatico */
}

/* Colori testo */
.text-color {
  color: red;                  /* Nome colore */
  color: #FF0000;              /* Esadecimale */
  color: #F00;                 /* Esadecimale abbreviato */
  color: rgb(255, 0, 0);       /* RGB */
  color: rgba(255, 0, 0, 0.5); /* RGBA con trasparenza 50% */
  color: hsl(0, 100%, 50%);    /* HSL (Hue, Saturation, Lightness) */
}

/* Colori sfondo */
.bg-solid {
  background-color: #3498db;   /* Sfondo blu solido */
}

.bg-transparent {
  background-color: rgba(52, 152, 219, 0.3);  /* Blu trasparente 30% */
}

/* Immagini di sfondo */
.bg-image {
  background-image: url('./immagini/pattern.png');
  background-size: cover;      /* Copre tutto l'elemento */
  background-position: center; /* Centra l'immagine */
  background-repeat: no-repeat; /* Non ripete l'immagine */
  background-attachment: fixed; /* Sfondo fisso allo scroll */
}

/* Gradienti lineari */
.bg-gradient-linear {
  background: linear-gradient(to right, #3498db, #2ecc71);
  /* Gradiente da sinistra (blu) a destra (verde) */

  background: linear-gradient(45deg, #FF6B6B, #4ECDC4, #45B7D1);
  /* Gradiente diagonale a 45° con 3 colori */
}

/* Gradienti radiali */
.bg-gradient-radial {
  background: radial-gradient(circle, #3498db, #2c3e50);
  /* Gradiente circolare da centro (blu) a bordi (scuro) */
}

/* Sfondo multiplo */
.bg-multiple {
  background: 
    linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)),  /* Overlay scuro */
    url('./hero.jpg');                                    /* Immagine sotto */
  background-size: cover;
  background-position: center;
}

flexbox_grid.css_code:
/* Container Flexbox */
.flex-container {
  display: flex;           /* Attiva Flexbox */
  flex-direction: row;     /* row (default), column, row-reverse, column-reverse */
  justify-content: center; /* Allineamento asse principale */
  /* Valori: flex-start, center, flex-end, space-between, space-around, space-evenly */

  align-items: center;     /* Allineamento asse trasversale */
  /* Valori: flex-start, center, flex-end, stretch (default), baseline */

  gap: 20px;               /* Spazio tra elementi (moderno) */
  /* Alternativa vecchia: margin sugli items */

  flex-wrap: wrap;         /* nowrap (default), wrap, wrap-reverse */
  /* wrap permette agli items di andare a capo */
}

/* Items Flexbox */
.flex-item {
  flex: 1;                 /* Shorthand per flex-grow, flex-shrink, flex-basis */
  /* flex: 1 = crescita uguale per tutti gli items */

  flex-basis: 200px;       /* Larghezza base dell'item */
  flex-grow: 1;            /* Fattore di crescita (0 = non cresce) */
  flex-shrink: 1;          /* Fattore di riduzione (0 = non si riduce) */

  align-self: flex-end;    /* Sovrascrive align-items per item singolo */
}

/* Esempi pratici */

/* Navbar orizzontale con logo e menu */
.navbar {
  display: flex;
  justify-content: space-between;  /* Logo a sinistra, menu a destra */
  align-items: center;
  padding: 20px;
  background-color: #333;
}

.navbar .logo {
  font-size: 24px;
  color: white;
}

.navbar .menu {
  display: flex;
  gap: 20px;  /* Spazio tra link del menu */
}

/* Card layout responsive */
.card-container {
  display: flex;
  flex-wrap: wrap;         /* Permette wrap su mobile */
  gap: 20px;
  justify-content: center;
}

.card {
  flex: 1 1 300px;         /* grow: 1, shrink: 1, basis: 300px */
  /* Larghezza minima 300px, cresce per riempire spazio */
  max-width: 400px;        /* Larghezza massima */
  padding: 20px;
  border: 1px solid #ddd;
}

/* Centra elemento verticalmente e orizzontalmente */
.center-box {
  display: flex;
  justify-content: center;  /* Centra orizzontalmente */
  align-items: center;      /* Centra verticalmente */
  height: 100vh;            /* Altezza schermo intero */
}

/* Container Grid */
.grid-container {
  display: grid;                      /* Attiva Grid */

  /* Definisci colonne */
  grid-template-columns: 1fr 2fr 1fr; /* 3 colonne: piccola, grande, piccola */
  /* 1fr = 1 frazione dello spazio disponibile */
  /* Se container è 1000px: col1=250px, col2=500px, col3=250px */

  grid-template-columns: repeat(3, 1fr);  /* 3 colonne uguali (shorthand) */
  grid-template-columns: 200px 1fr 200px; /* Sidebars fisse, main fluido */

  /* Definisci righe */
  grid-template-rows: auto 1fr auto;  /* Header auto, Main cresce, Footer auto */

  /* Spazio tra celle */
  gap: 20px;                 /* gap sia righe che colonne */
  row-gap: 20px;             /* gap solo righe */
  column-gap: 30px;          /* gap solo colonne */
}

/* Items Grid: posizionamento manuale */
.grid-item-header {
  grid-column: 1 / 4;  /* Dalla colonna 1 alla 4 (occupa tutte e 3 colonne) */
  /* Alternativa: grid-column: 1 / -1 (dall'inizio alla fine) */
  grid-row: 1;         /* Riga 1 */
}

.grid-item-sidebar {
  grid-column: 1;      /* Colonna 1 */
  grid-row: 2;         /* Riga 2 */
}

.grid-item-main {
  grid-column: 2;      /* Colonna 2 */
  grid-row: 2;         /* Riga 2 */
}

.grid-item-footer {
  grid-column: 1 / -1; /* Tutte le colonne */
  grid-row: 3;         /* Riga 3 */
}

/* Layout pagina completo */
.page-layout {
  display: grid;
  grid-template-columns: 200px 1fr;  /* Sidebar 200px, Main resto */
  grid-template-rows: 80px 1fr 60px; /* Header 80px, Main cresce, Footer 60px */
  grid-template-areas:
    "header header"
    "sidebar main"
    "footer footer";
  gap: 20px;
  min-height: 100vh;  /* Altezza minima schermo intero */
}

.page-header { grid-area: header; }
.page-sidebar { grid-area: sidebar; }
.page-main { grid-area: main; }
.page-footer { grid-area: footer; }

/* Grid responsive con auto-fill */
.card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  /* Crea colonne di minimo 300px, riempie automaticamente lo spazio */
  /* Su desktop: 3-4 colonne. Su mobile: 1 colonna */
  gap: 20px;
}

/* Grid con named lines */
.advanced-grid {
  display: grid;
  grid-template-columns: [start] 1fr [middle] 2fr [end];
  grid-template-rows: [top] auto [content] 1fr [bottom];
}

.item {
  grid-column: start / middle;  /* Usa named lines */
  grid-row: top / bottom;
}

/* USO FLEXBOX: Navbar (componente 1D) */
.navbar-flex {
  display: flex;
  justify-content: space-between;  /* Logo vs Menu */
  align-items: center;
  padding: 20px;
}

.navbar-flex .menu {
  display: flex;
  gap: 20px;  /* Link affiancati */
}

/* USO GRID: Layout pagina (struttura 2D) */
.page-grid {
  display: grid;
  grid-template-columns: 250px 1fr;  /* Sidebar + Main */
  grid-template-rows: auto 1fr auto; /* Header, Main, Footer */
  grid-template-areas:
    "header header"
    "sidebar main"
    "footer footer";
  gap: 20px;
  min-height: 100vh;
}

/* COMBINAZIONE: Card container con Grid, Card interna con Flex */
.card-container-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
}

.card-flex {
  display: flex;
  flex-direction: column;  /* Stack verticale: immagine, titolo, testo, button */
  justify-content: space-between;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 8px;
}

.card-flex img {
  width: 100%;
  height: 200px;
  object-fit: cover;
}

.card-flex .card-content {
  flex: 1;  /* Cresce per riempire spazio */
}

.card-flex button {
  margin-top: auto;  /* Pulsante sempre in fondo */
}

/* GUIDA: Quando usare quale? */

/* USA FLEXBOX per: */
.use-flex-examples {
  /* 1. Navbar orizzontale */
  display: flex;
  justify-content: space-between;

  /* 2. Lista di pulsanti affiancati */
  display: flex;
  gap: 10px;

  /* 3. Centrare elemento */
  display: flex;
  justify-content: center;
  align-items: center;

  /* 4. Form con label e input allineati */
  display: flex;
  align-items: center;
  gap: 10px;

  /* 5. Footer con link distribuiti */
  display: flex;
  justify-content: space-evenly;
}

/* USA GRID per: */
.use-grid-examples {
  /* 1. Layout pagina completo (header, sidebar, main, footer) */
  display: grid;
  grid-template-areas: "header" "main" "footer";

  /* 2. Gallery immagini con colonne variabili */
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));

  /* 3. Dashboard con pannelli */
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(3, 200px);

  /* 4. Calendario */
  display: grid;
  grid-template-columns: repeat(7, 1fr);  /* 7 giorni */

  /* 5. Form complesso con etichette e campi su griglia */
  display: grid;
  grid-template-columns: 150px 1fr;
  gap: 10px;
}

responsive_design.css_example:
/* MOBILE-FIRST: Base styles per mobile (320px+) */
body {
  font-size: 14px;            /* Font piccolo per mobile */
  line-height: 1.5;
  padding: 10px;
}

.container {
  width: 100%;                /* Schermo intero su mobile */
  padding: 0 15px;
}

.grid {
  display: grid;
  grid-template-columns: 1fr; /* 1 colonna su mobile */
  gap: 15px;
}

.nav {
  display: flex;
  flex-direction: column;     /* Menu verticale su mobile */
  gap: 10px;
}

.button {
  padding: 12px 20px;
  font-size: 16px;
  min-height: 44px;           /* Touch target minimo 44px */
  min-width: 44px;
}

/* TABLET: 768px e oltre */
@media (min-width: 768px) {
  body {
    font-size: 16px;          /* Font più grande */
    padding: 20px;
  }

  .container {
    width: 90%;
    max-width: 720px;
    margin: 0 auto;           /* Centra container */
  }

  .grid {
    grid-template-columns: repeat(2, 1fr);  /* 2 colonne su tablet */
    gap: 20px;
  }

  .nav {
    flex-direction: row;      /* Menu orizzontale su tablet */
    gap: 20px;
  }
}

/* DESKTOP: 1024px e oltre */
@media (min-width: 1024px) {
  body {
    font-size: 18px;          /* Font ancora più grande */
    padding: 30px;
  }

  .container {
    width: 80%;
    max-width: 1200px;
  }

  .grid {
    grid-template-columns: repeat(3, 1fr);  /* 3 colonne su desktop */
    gap: 30px;
  }

  .nav {
    justify-content: space-between;
  }

  /* Effetti hover solo su desktop (non su touch) */
  .button:hover {
    background-color: #0056b3;
    transform: translateY(-2px);
  }
}

/* LARGE DESKTOP: 1920px e oltre */
@media (min-width: 1920px) {
  .container {
    max-width: 1600px;
  }

  .grid {
    grid-template-columns: repeat(4, 1fr);  /* 4 colonne su schermi grandi */
  }
}

/* Esempio COMPLETO: Card responsive */
.card {
  /* Mobile: card a schermo intero */
  width: 100%;
  padding: 15px;
  margin-bottom: 15px;
  border: 1px solid #ddd;
  border-radius: 8px;
}

@media (min-width: 768px) {
  /* Tablet: card affiancate (2 per riga) */
  .card-container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 20px;
  }

  .card {
    width: auto;              /* Width automatica dalla grid */
    padding: 20px;
  }
}

@media (min-width: 1024px) {
  /* Desktop: 3 card per riga */
  .card-container {
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
  }
}

/* CSS: Immagini responsive base */
img {
  max-width: 100%;      /* Non superare larghezza container */
  height: auto;         /* Mantieni proporzioni */
  display: block;       /* Rimuovi spazio sotto immagine inline */
}

/* Object-fit: controllo cropping */
.img-cover {
  width: 100%;
  height: 300px;        /* Altezza fissa */
  object-fit: cover;    /* Crop immagine per riempire (come background-size: cover) */
  object-position: center;  /* Centra immagine */
}

.img-contain {
  width: 100%;
  height: 300px;
  object-fit: contain;  /* Immagine intera visibile, può avere spazio vuoto */
}

/* Aspect ratio: evita layout shift durante caricamento */
.img-ratio {
  aspect-ratio: 16 / 9;  /* Rapporto 16:9 (video) */
  width: 100%;
  object-fit: cover;
}

/* Placeholder durante caricamento */
.img-wrapper {
  background-color: #f0f0f0;  /* Grigio chiaro */
  aspect-ratio: 16 / 9;
  position: relative;
}

.img-wrapper img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* HTML: <picture> element per breakpoint diversi */
<picture>
  <!-- Desktop: immagine grande orizzontale -->
  <source 
    media="(min-width: 1024px)" 
    srcset="immagine-large.jpg" 
  />

  <!-- Tablet: immagine media -->
  <source 
    media="(min-width: 768px)" 
    srcset="immagine-medium.jpg" 
  />

  <!-- Mobile: immagine piccola (default) -->
  <img 
    src="immagine-small.jpg" 
    alt="Descrizione accessibile" 
    loading="lazy"
  />
</picture>

/* HTML: srcset per risoluzioni diverse (retina) */
<img 
  src="foto.jpg" 
  srcset="foto.jpg 1x, foto@2x.jpg 2x, foto@3x.jpg 3x"
  alt="Foto descrittiva"
  loading="lazy"
/>
/* Browser sceglie automaticamente: 1x per schermi normali, 2x per retina */

/* HTML: srcset con larghezze */
<img 
  src="foto-800.jpg"
  srcset="
    foto-400.jpg 400w,
    foto-800.jpg 800w,
    foto-1200.jpg 1200w
  "
  sizes="
    (max-width: 768px) 100vw,
    (max-width: 1024px) 50vw,
    33vw
  "
  alt="Foto responsive"
/>
/* Browser sceglie immagine basandosi su larghezza viewport e sizes */

/* Formato moderno WebP con fallback */
<picture>
  <source srcset="foto.webp" type="image/webp" />
  <source srcset="foto.jpg" type="image/jpeg" />
  <img src="foto.jpg" alt="Fallback per browser vecchi" />
</picture>

/* Background image responsive con media query */
.hero {
  background-image: url('hero-mobile.jpg');
  background-size: cover;
  background-position: center;
  height: 50vh;
}

@media (min-width: 768px) {
  .hero {
    background-image: url('hero-tablet.jpg');
    height: 70vh;
  }
}

@media (min-width: 1024px) {
  .hero {
    background-image: url('hero-desktop.jpg');
    height: 100vh;
  }
}

/* Lazy loading con intersection observer (JavaScript) */
.lazy-img {
  opacity: 0;
  transition: opacity 0.3s;
}

.lazy-img.loaded {
  opacity: 1;
}
